import React from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import {useSelector,useDispatch} from "react-redux"
function SignUp() {
  let [udetails, setUdetails] = useState({
    name: "",
    email: "",
    pass: "",
    cpass: "",
  });

  

  let nav=useNavigate();
  let [passCheck, setPassCheck] = useState(false);

  let handleInput = (event) => {
    if (event.target.name === "name") {
      setUdetails({ ...udetails, name: event.target.value });
    }
    if (event.target.name === "email") {
      setUdetails({ ...udetails, email: event.target.value });
    }
    if (event.target.name === "pass") {
      if (udetails.cpass !== event.target.value) {
        setPassCheck(true);
      } else {
        setPassCheck(false);
      }
      setUdetails({ ...udetails, pass: event.target.value });
    }
    if (event.target.name === "cpass") {
      if (udetails.pass !== event.target.value) {
        setPassCheck(true);
      } else {
        setPassCheck(false);
      }
      setUdetails({ ...udetails, cpass: event.target.value });
    }
  };

  let formhandle = (event) => {
    event.preventDefault();
    if(passCheck){
      alert("Password not matched");
    }
    else{
      let obj={
        name: udetails.name,
        email: udetails.email,
        pass: udetails.pass
      }
      nav("/")
      alert("ACCOUNT SUCCESSFULLY CREATED")
    }
  };


  return (
    <div className="signup" >
      <h1>REGISTRATION</h1>
      <form action="" onSubmit={formhandle}>
        <input
          size={40}
          type="text"
          placeholder="ENTER YOUR NAME"
          name="name"
          onChange={handleInput}
          value={udetails.name}
          required
        />
        <input
          size={40}
          type="email"
          placeholder="ENTER YOUR EMAIL"
          name="email"
          onChange={handleInput}
          value={udetails.email}
          required

        />
        <input
          size={40}
          type="password"
          placeholder="CREATE  PASSWORD"
          name="pass"
          onChange={handleInput}
          required
          value={udetails.pass}
        />
        <input
          size={40}
          type="password"
          placeholder="CONFIRM PASSWORD"
          name="cpass"
          onChange={handleInput}
          required
          value={udetails.cpass}
        />
        {passCheck && (
          <span style={{ color: "red" }}>*password doesn't match</span>
        )}
        <button>REGISTER</button>
      </form>
    </div>
  );
}

export default SignUp;
