import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useDispatch } from 'react-redux';
function Cards(props) {
  // let user=useSelector(state=>state.loginUser);
  let dis=useDispatch()

    let addtocart=()=>{
      dis({type: 'addtocart',payload:props})
    }

  return (
    <Card style={{ width: '18rem' ,border:"solid"}}>
      <Card.Img variant="top" src={props.ele.thumbnail} height={300} />
      <Card.Body>
        <Card.Title>{props.ele.title}</Card.Title>
        <Card.Body>
            <strong>${props.ele.price}</strong>
        </Card.Body>
        <Card.Text>
          {props.ele.description}
        </Card.Text>
        <Button variant="primary" onClick={addtocart}>ADD TO CART</Button>
      </Card.Body>
    </Card>
  );
}

export default Cards;
