import React, { useContext } from 'react'
import { store } from './Context'


function Context2() {
    const[value,setvalue]  = useContext(store);
  return (
    <div>
        <button onClick={()=>setvalue(value+1)}>+</button>
        <span>{value}</span>
        <button onClick={()=>setvalue(value-1)}>-</button>
    </div>
  )
}

export default Context2
