import React from "react";
import {useDispatch, useSelector} from 'react-redux'
import {useNavigate} from 'react-router-dom'

function Login() {
   let userArray= useSelector((state)=>state.userReducer)
   let nav=useNavigate();
   let dis=useDispatch();

   let LoginCheck=(event)=>{
        event.preventDefault();
        userArray.forEach(element => {
            if(element.details.email===event.target.email.value && element.details.pass===event.target.pass.value){
                    dis({type:"loggedin",payload:element});
                    nav("/products");
                    alert("LOGIN SUCCESSFUL")
            }
        });
   }

  return (
    <div>
        {console.log(userArray)}
        <h1>LOGIN</h1>
      <form action="" onSubmit={LoginCheck}>
        <input type="email" placeholder="EMAIL" name="email" />
        <input type="password" placeholder="PASSWORD" name="pass"/>
        <button >LOGIN</button>
      </form>
    </div>
  );
}

export default Login;
