import { combineReducers } from "redux";

let initialstate = {
  recemonded: "All Products",
  category: "",
  price: 0,
  color: "",
};

let filterreducer = (state = initialstate, action) => {
  switch (action.type) {
    case "recomended":
      return { ...state, recemonded: action.payload };
    default:
      return state;
  }
};

let rootreducer = combineReducers({ filterreducer });

export default rootreducer;
