import { combineReducers } from "redux";

let users = [];
let loggedInUser = {};
let userReducer = (state = users, action) => {
  switch (action.type) {
    case "addNewUser":
      let obj = {
        details: {
          name: action.payload.name,
          email: action.payload.email,
          pass: action.payload.pass,
        },
        cart: [],
      };
      return [...users, obj];
    default:
      return state;
  }
};

let loginUser = (state = loggedInUser, action) => {
  switch (action.type) {
    case "loggedin":
      // let user = users.find((user) => {
      //   return (
      //     action.payload.email === user.email &&
      //     action.payload.pass === user.pass
      //   );
      // });
      return action.payload;
    case "addtocart":
      state.cart.push(action.payload);
      return { ...state };
    default:
      return state;
  }
};

let rootReducer = combineReducers({ userReducer, loginUser });

export default rootReducer;
