import React from "react";
import "./products.css";
import Card from "./Card";
import { useSelector } from "react-redux";

function Products() {
  let state=useSelector((state)=>state.filterreducer);
  let data=useSelector((state)=>state.datareducer);
  if(state.recomended!=="All Products"){
    data=data.filter(ele=>ele.company===state.recomended)
    console.log(data);
  }
  if(state.input!==""){
    data=data.filter(ele=>ele.title.toLowerCase().includes(state.input.toLowerCase()))
    console.log(data);
  }

  if(state.color!=='All'){
    data=data.filter(ele=>ele.color===state.color.toLowerCase())
    console.log(data);
  }
  return (
    <section className="card-container">
      {console.log(state)}
      {data.length!==0 ? data.map((ele,i)=>{
        return <Card ele={ele}></Card>
      }):<h1>No data found </h1>}
    </section>
  );
}

export default Products;
