import React from 'react'
import { useState } from 'react'

function SignUp() {

  let [udetails,setUdetails]=useState({
    name:"",
    email:"",
    pass:"",
    cpass:""
  })

  let [passCheck,setPassCheck]=useState(false)

  let handleInput=(event)=>{
    if(event.target.name==='name') {
      setUdetails({...udetails,name:event.target.value})
    }
    if(event.target.name==='email') {
      setUdetails({...udetails,email:event.target.value})
    }
    if(event.target.name==='pass') {
      if(udetails.cpass!==event.target.value) {
        setPassCheck(true)
      }
      else{
        setPassCheck(false)
      }
      setUdetails({...udetails,pass:event.target.value})
    }
    if(event.target.name==='cpass') {
      if(udetails.pass!==event.target.value) {
        setPassCheck(true)
      }
      else{
        setPassCheck(false)
      }
      setUdetails({...udetails,cpass:event.target.value})
    }
    
  }

let formhandle=(event)=>{
  event.preventDefault();
}

  return (
    <div className='signup' onSubmit={formhandle}>
      <h1>REGISTRATION</h1>
      <form action="">
        <input size={40} type="text" placeholder='ENTER YOUR NAME'  name='name' onChange={handleInput}  value={udetails.name}/>
        <input size={40} type="email" placeholder='ENTER YOUR EMAIL' name='email' onChange={handleInput} value={udetails.email}/>
        <input size={40} type="password" placeholder='CREATE  PASSWORD' name='pass' onChange={handleInput} value={udetails.pass}/>
        <input size={40} type="password" placeholder='CONFIRM PASSWORD' name='cpass' onChange={handleInput} value={udetails.cpass}/>
        {passCheck && <span style={{color:'red'}}>*password doesn't match</span>}
        <button>REGISTER</button>
      </form>

    </div>
  )
}

export default SignUp
